public class RemovePalindromicSubsequences_1332 {
  public int removePalindromeSub(String s) {
    if (s.isEmpty()) {
      return 0;
    }

    return isPalindrome(s) ? 1 : 2;
  }

  boolean isPalindrome(String s) {
    int left = 0;
    int right = s.length() - 1;

    while (left < right) {
      if (s.charAt(left++) != s.charAt(right--)) {
        return false;
      }
    }

    return true;
  }
}
